const axios = require('axios');
const Jimp = require('jimp');

const LOGOS = {
    spotify: "data:image/svg+xml;base64,",
    ytmusic: "data:image/svg+xml;base64,",
    yandex: "data:image/svg+xml;base64,",
    apple: "data:image/svg+xml;base64,",
    soundcloud: "data:image/svg+xml;base64,"
};

const escapeHtml = (str) => str.replace(/[<>"]/g, (m) => ({
    '<': '&lt;',
    '>': '&gt;',
    '"': '&quot;'
}[m]));

const validateLink = (link) => {
    try {
        new URL(link);
        return true;
    } catch {
        return false;
    }
};

const fetchImageAsBase64 = async (imageUrl) => {
    try {
        const response = await axios.get(imageUrl, { responseType: 'arraybuffer' });
        const image = await Jimp.read(response.data);
        image.cover(240, 240);
        return await image.getBase64Async(Jimp.MIME_JPEG);
    } catch {
        const placeholder = await Jimp.create(240, 240, 0x333333ff);
        return await placeholder.getBase64Async(Jimp.MIME_JPEG);
    }
};

const extractDominantColor = async (imageUrl) => {
    try {
        const response = await axios.get(imageUrl, { responseType: 'arraybuffer' });
        const image = await Jimp.read(response.data);
        const avg = image.clone().resize(1, 1).getPixelColor(0, 0);
        const { r, g, b } = Jimp.intToRGBA(avg);
        return `rgb(${Math.floor(r * 0.8)}, ${Math.floor(g * 0.8)}, ${Math.floor(b * 0.8)})`;
    } catch {
        return '#333333';
    }
};

const formatTime = (ms) => {
    if (!ms) return "--:--";
    const min = Math.floor(ms / 60000);
    const sec = Math.floor((ms % 60000) / 1000);
    return `${min}:${sec.toString().padStart(2, '0')}`;
};

module.exports = {
    LOGOS,
    escapeHtml,
    validateLink,
    fetchImageAsBase64,
    extractDominantColor,
    formatTime
};
